def transform(sequence):
    first_non_zero = next((i for i, x in enumerate(sequence) if x != '0'), None)
    return sequence[:first_non_zero] + '0' + sequence[first_non_zero:-1] + sequence[first_non_zero]

def transform(sequence):
    if len(sequence) < 2:
       return sequence
    else:
       return sequence[0] + sequence[1:-1].replace("0", sequence[0]) + sequence[-1:]

def transform(sequence):
    index = sequence.find('0')    
    return sequence[:index-1] + '0' + sequence[index-1:]
