def transform(sequence):
    first_non_zero_index = next((i for i, x in enumerate(sequence) if x != '0'), None)
    if first_non_zero_index is None or first_non_zero_index == 0:
        return sequence
    else:
        return sequence[:first_non_zero_index-1] + sequence[first_non_zero_index] + '0' + sequence[first_non_zero_index+1:]
