def transform(sequence):
    max_number = max(sequence)
    new_sequence = []
    flag = False
    for num in sequence:
        if num == max_number:
            flag = True
        if flag:
            new_sequence.append(max_number)
        else:
            new_sequence.append(num)
    return "".join(new_sequence)

def transform(sequence):
    transformed = sequence.rstrip('2').rstrip('0') + '2'
    remaining_zero_count = sequence.count('0') - transformed.count('0')
    return transformed.replace('0', '2', remaining_zero_count)
