def transform(sequence):
    map = {"7": "2", "0": "0", "00": "00", "77": "22", "07": "06", "70": "20"}
    sequence = sequence.replace('07', 'X')
    sequence = sequence.replace('70', 'Y')
    for key, value in map.items():
        sequence = sequence.replace(key, value)
    sequence = sequence.replace('X', '06')
    sequence = sequence.replace('Y', '20')
    return sequence

