def transform(sequence):
    transformation_dict = {"0":"0", "5":"1", "55":"44", "50":"90"}
    sequence_list = list(sequence)
    for i in range(len(sequence_list)-1):
        if sequence_list[i] + sequence_list[i+1] in transformation_dict:
            sequence_list[i] = transformation_dict[sequence_list[i] + sequence_list[i+1]]
            sequence_list[i+1] = ''
    sequence = ''.join(sequence_list)
    sequence = sequence.replace("5", "4")
    return sequence
