def transform(sequence):
    mapping = {'0': '0', '1': '5', '01': '02', '10': '90', '11': '99'}
    transformed = ''.join(mapping[sequence[i:i+2]] if sequence[i:i+2] in mapping else mapping[sequence[i]] for i in range(0, len(sequence), 2))
