def transform(sequence):
    index = sequence.find('8') if '8' in sequence else sequence.find('6')
    if index == -1:
        index = sequence.find('1')

    return sequence[:index] + sequence[index:].replace("0", "").ljust(len(sequence)-index, '0')

def transform(sequence):
    seq_list = list(sequence)
    change = False
    for i in range(len(seq_list)):
        if seq_list[i] != '0' and not change:
            change = True
        elif seq_list[i] == '0' and change:
            seq_list[i] = '0'
        elif seq_list[i] != '0' and change:
            seq_list[i] = '0'
    return ''.join(seq_list)

def transform(sequence):
    transformed_seq = ''
    for i in range(len(sequence)):
        if i < sequence.rfind('0'):
            transformed_seq += '0'
        else:
            transformed_seq += sequence[i]
    return transformed_seq

def transform(sequence):
    last_index = sequence.rfind('0')
    return sequence[:last_index+1] + '0'* (len(sequence) - last_index - 1)

def transform(sequence):
    idx = sequence.rfind('0', 0, sequence.rindex('8')+1 if '8' in sequence else len(sequence))
    return sequence[:idx+1] + '0'*(len(sequence)-idx-1)

def transform(sequence):
    mid_index = len(sequence) // 2
    return sequence[:mid_index] + sequence[mid_index:].replace(sequence[0], '0')
