def transform(sequence):
    transform_point = sequence.find('0', sequence.find('8')+1)
    return sequence[:transform_point] + '0'*(len(sequence) - transform_point)

def transform(sequence):
    index = sequence.rfind('8') + 1
    return sequence[:index] + '0' * (len(sequence) - index)

def transform(sequence):
    sequence = list(sequence)
    while '08' in ''.join(sequence):
        index = sequence.index('08') + 1
        sequence[index] = '0'
    return ''.join(sequence)
