def transform(sequence):
    transformed_sequence = ''
    sequence = list(map(int, sequence))
    index_of_first_2 = sequence.index(2)
    for i in range(len(sequence)):
        if sequence[i] != 2 and i > index_of_first_2:
            transformed_sequence += '0'
        else:
            transformed_sequence += str(sequence[i])
    return transformed_sequence

def transform(sequence):
    index = sequence.rfind('2')
    return sequence[:index + 1] + ''.join('0' for _ in sequence[index + 1:])

def transform(sequence):
    index = sequence.rfind('2' if '2' in sequence else '7')
    return sequence[:index+1] + '0' * (len(sequence) - index - 1)

