    i = 0
    while i < len(sequence):
        current_digit = sequence[i]
        if i < len(sequence) - 1 and sequence[i + 1] != '0':
            transformed += current_digit
        elif i < len(sequence) - 2 and sequence[i + 2] != '0':
            transformed += current_digit * 2
        elif i < len(sequence) - 3 and sequence[i + 3] != '0':
            transformed += current_digit * 3
        else:
            transformed += current_digit
        i += 1
    return transformed

def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)-1):
        if sequence[i] != '0' and sequence[i+1] == '0':
