def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence) - 5):
        if sequence[i:i+5] == ['0', '0', '0', '0', '0']:
            sequence[i:i+5] = [sequence[i-1]] * 5
    return ''.join(sequence)

