def transform(sequence):
    transformed = []
    for i in range(len(sequence)):
        if sequence[i] != '0':
            if i >= 5:
                transformed = transformed[:i - 5] + [sequence[i]] * 5 + transformed[i:]
            else:
                transformed = [sequence[i]] * (5 - i) + transformed
    return "".join(transformed)
