def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)):
        if sequence[i] == '0':
            sequence[i] = '6' if sequence[i-1] == '6' else '0'
        elif sequence[i] == '2' or sequence[i] == '3':
            sequence[i] = '6'
    return ''.join(sequence)

def transform(sequence):
    split_sequence = [sequence[i: i + 4] for i in range(0, len(sequence), 4)]
    transformed_sequence = ''
    for item in split_sequence:
        if item[0] != '0':
            transformed_sequence += item[0] + item[1]*3
        else:
            transformed_sequence += item
    return transformed_sequence

def transform(sequence):
    transformation = sequence.replace("0", "6").replace("2", "22").replace("3", "33")
    return transformation
