def transform(sequence):
    start = sequence.find('0')
    if start == -1:
        return sequence
    else:
        return '0'*start + sequence.replace('0', '') + '0'*(len(sequence)-start-1)

def transform(sequence):
    length = len(sequence)
    zeroes = sequence.count('0')
    return '0'*zeroes + sequence.replace('0', '') + '0'*(length-zeroes-1)
