    return sequence[:first_non_zero_index] + '0' + sequence[first_non_zero_index:-1]

def transform(sequence):
    count_zero = sequence.count('0')
    count_non_zero = len(sequence) - count_zero
    return '0'* (count_zero + 1) + '0' + str(count_non_zero)*count_non_zero + '0'*(count_zero - 1)

def transform(sequence):
    non_zero_index = sequence.find(next(filter(str.isdigit, sequence)))
    return sequence[:non_zero_index] + "0" + sequence[non_zero_index:-1]

def transform(sequence):
    index = sequence.find('0', 1)
    return sequence[:index - 1] + '0' + sequence[index - 1:]

def transform(sequence):
    ind = sequence.find('0', 1)
    return '0' * (ind + 1) + sequence[ind+1:].replace('0', '') + '0' * (len(sequence) - len(sequence[ind+1:]))
