def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)):
        if(sequence[i] == '8'):
            sequence[i] = '7'
        elif(sequence[i] == '6'):
            sequence[i] = '8'
        elif(sequence[i] == '4'):
            sequence[i] = '6'
        else:
            continue
    sequence = ''.join(sequence)
    return sequence
