def transform(sequence):
    non_zero_found = False
    transformed_seq = ''
    for i in sequence:
        if i != '0' and not non_zero_found:
            non_zero_found = True
        if non_zero_found:
            transformed_seq += i
    transformed_seq = '0'*(len(sequence)-len(transformed_seq)) + transformed_seq
    return transformed_seq

