def transform(sequence):
    if not sequence:
        return sequence

    count_zero = sequence.count('0')
    count_nonzero = len(sequence) - count_zero

    return '0' * count_zero + str(int(sequence)//10**count_zero) * count_nonzero

def transform(sequence):
    index = sequence.index(next(x for x in sequence if x != '0'))
    sequence = list(sequence)
    sequence[index], sequence[0] = sequence[0], sequence[index]
    return ''.join(sequence)
