    index_of_last_non_zero = len(sequence.rstrip('0')) - 1
    if sequence[index_of_last_non_zero] == '4':
        return sequence[:index_of_last_non_zero] + '3' + sequence[index_of_last_non_zero+1:]
    elif sequence[index_of_last_non_zero] == '3':
        return sequence[:index_of_last_non_zero] + '2' + sequence[index_of_last_non_zero+1:]
