def transform(sequence):
    seq = list(sequence)
    for i in range(len(seq)):
        if i > 0 and seq[i] != "0" and seq[i-1] == "0":
            for j in range(i+1, len(seq)):
                if seq[j] == "0":
                    break
                seq[j] = seq[i]
    return "".join(seq)

