def transform(sequence):
    trans_dict = {'0':'0','4':'1','44':'66'}
    sequence = sequence[::-1]
    transformed = ''
    while sequence:
        if sequence[:2] in trans_dict:
            transformed += trans_dict[sequence[:2]]
            sequence = sequence[2:]
        elif sequence[0] in trans_dict:
            transformed += trans_dict[sequence[0]]
            sequence = sequence[1:]
    return transformed[::-1]
