def transform(sequence):
    first_five = sequence.find('5')
    last_five = sequence.rfind('5')
    first_eight = sequence.find('8')
    last_eight = sequence.rfind('8')

    if first_five != -1 and last_five != -1:
        sequence = sequence[:first_five+1] + '0' * (last_five-first_five-1) + sequence[last_five:]
    if first_eight != -1 and last_eight != -1:
        sequence = sequence[:first_eight+1] + '0' * (last_eight-first_eight-1) + sequence[last_eight:]

    return sequence

def transform(sequence):
    start = sequence.find('0', sequence.find('8')) 
    end = sequence.rfind('8', sequence.find('5'))
    if '8' in sequence:
        sequence = sequence[:start+1] + '0'*(end-start-1) + sequence[end:]
    start = sequence.find('0', sequence.find('5')) 
    end = sequence.rfind('5', sequence.rfind('0'))
    if '5' in sequence:
        sequence = sequence[:start+1] + '0'*(end-start-1) + sequence[end:]
