def transform(sequence):
    sequence = list(sequence)
    first_one = sequence.index('1') if '1' in sequence else -1
    first_seven = sequence.index('7') if '7' in sequence else -1
    if first_one != -1:
        sequence[first_one+1:-1] = ['0']*(len(sequence)-first_one-2)
    if first_seven != -1:
        sequence[first_seven+1:-1] = ['0']*(len(sequence)-first_seven-2)
