def transform(sequence):
    sequence = str(sequence)
    transformed = ""
    indexes = [i for i, x in enumerate(sequence) if x != "0"]
    for i in range(len(sequence)):
        if i in indexes:
            transformed += sequence[i]
        else:
            if len(indexes) != 0 and i > max(indexes):
                transformed += "0"
            else:
                transformed += sequence[indexes[0]]
    return transformed

