    for i in range(len(sequence)):
        if sequence[i] not in ['0', '3', '6']:
            sequence[i] = sequence[i-1]
    return ''.join(sequence)

def transform(sequence):
    new_sequence = ''
    for char in sequence:
        if char == '2' or char == '6' or char == '5' or char == '7':
            new_sequence += '3'
        else:
            new_sequence += char
    return new_sequence
