def transform(sequence):
    ones_index = sequence.find('1')
    fives_index = sequence.find('5')
    sixes_index = sequence.find('6')
    if ones_index != -1:
        sequence = sequence[:ones_index] + '00' + sequence[ones_index:-2]
    if fives_index != -1: 
        sequence = sequence[:fives_index] + '00' + sequence[fives_index:-2]
