def transform(sequence):
    zeroPart = ''
    otherPart = ''
    prepareOther = False
    partSplitter = ''

    for c in reversed(sequence):
        if c == '0':
            if prepareOther:
                zeroPart += '0'
            else:
                otherPart = '0' + otherPart
        else:
            prepareOther = True
            otherPart = c + otherPart
            if partSplitter == '':
                partSplitter = c

    return zeroPart + partSplitter + otherPart.replace(partSplitter,'',1)
