def transform(sequence):
    nine_index = sequence.index('9')
    rest_part = sequence[nine_index+1:]
    sequence = sequence[:nine_index+1]
    non_zero_part = ""

    for char in sequence:
        if char != '0':
            non_zero_part += char

    sequence = sequence.replace(non_zero_part, '0' * len(non_zero_part))

    return sequence + non_zero_part + rest_part
