def transform(sequence):
    trans_dict = {'0': '0', '2': '8', '20': '55', '200': '880', '220': '880', '2000': '8800', '2200': '8800', '20000': '88000'}
    new_seq = ''

    i = 0
    while i < len(sequence):
        if sequence[i:i+5] in trans_dict.keys():
            new_seq += trans_dict[sequence[i:i+5]]
            i += 5
        elif sequence[i:i+4] in trans_dict.keys():
            new_seq += trans_dict[sequence[i:i+4]]
            i += 4
        elif sequence[i:i+3] in trans_dict.keys():
            new_seq += trans_dict[sequence[i:i+3]]
            i += 3
        elif sequence[i:i+2] in trans_dict.keys():
            new_seq += trans_dict[sequence[i:i+2]]
            i += 2
        else:
            new_seq += trans_dict[sequence[i]]
            i += 1

    return new_seq
