def transform(sequence):
    transformed_sequence = ''
    for char in sequence:
        if char == '0':
            transformed_sequence += '0'
        elif char == '4':
            transformed_sequence += '6'
        else:
            transformed_sequence += str(int(char) * 2)
    return transformed_sequence

def transform(sequence):
    transform_dict = {'0': '0', '4': '6', '44': '22', '444': '666'}
    for original, transformed in transform_dict.items():
        sequence = sequence.replace(original, transformed)
    return sequence

def transform(sequence):
    transformation = {'4': '2', '0': '0', '2': '8', '8': '4', '6': '6'}
    return ''.join([transformation[i] for i in sequence.replace('4', '6').replace('0', '2')])
