def transform(sequence):
    transform_dict = {"0":0, "5":9, "50":22, "55":11}
    transformed_sequence = ''

    while sequence:
        if sequence[:2] in transform_dict:
            transformed_sequence += str(transform_dict[sequence[:2]])
            sequence = sequence[2:]
        else:
            transformed_sequence += str(transform_dict[sequence[0]])
            sequence = sequence[1:]

    return transformed_sequence
