def transform(sequence):
    sequence = list(str(sequence))
    last_zero = max(loc for loc, val in enumerate(sequence) if val == '0')
    sequence.insert(last_zero+1, '6')
    sequence[last_zero] = '0'
    return ''.join(sequence)

